<?php $youremail = "samuca@samucamg.com";include "db.php";if($type=!"premium"){	echo "Sorry but you do not have access to view this page";}else{$mysql=mysql_connect(mysql_host, mysql_user, mysql_password) or die(mysql_error());mysql_select_db(mysql_db) or die(mysql_error());	$i=0;?><center>On this page you can see the network status. It checks if the downloadservers are working or not. If a server is offline it will be displayed and updated in our database. We check every 7 minutes on our downloadservers to see if there are no problems.<br /><br /><table>	<tr>		<th>URL</th>		<th>ServerName</th>		<th>Type</th>		<th>Status</th>		<th>Visit</th>	</tr><?php$source = mysql_query("SELECT * FROM servers where harddisable!='1'") or die(mysql_error()); while($row = mysql_fetch_array( $source )) {$Id = $row['Id'];$url = $row['ServerLink'];$name = $row['name'];$type = $row['type'];?><?php $i++; ?><?php if($i%2==0) { $oddeven="even"; } else { $oddeven="odd"; } ?>	<tr class="<?php echo $oddeven; ?>">		<td><?php echo "$url"; ?></td>		<td><?php echo "$name"; ?></td>		<td><?php echo "$type"; ?></td>		<?php$curl = curl_init();curl_setopt($curl, CURLOPT_SSL_VERIFYPEER, FALSE);curl_setopt($curl, CURLOPT_HEADER, 1);//curl_setopt($curl, CURLOPT_POST, false);curl_setopt($curl, CURLOPT_RETURNTRANSFER, true);curl_setopt($curl, CURLOPT_TIMEOUT, 20);curl_setopt($curl, CURLOPT_USERAGENT, "Mozilla/4.0 (compatible; MSIE 5.01; Windows NT 5.0)");curl_setopt($curl, CURLOPT_FOLLOWLOCATION, false);curl_setopt($curl, CURLOPT_URL, "http://$url/download.php?id=abc");$xxx = curl_exec($curl);$check1 = explode('Invalid',$xxx);if (isset($check1[1])) {$working = "1";curl_close($curl);$curl2 = curl_init();curl_setopt($curl2, CURLOPT_SSL_VERIFYPEER, FALSE);curl_setopt($curl2, CURLOPT_HEADER, 1);//curl_setopt($curl, CURLOPT_POST, false);curl_setopt($curl2, CURLOPT_RETURNTRANSFER, true);curl_setopt($curl2, CURLOPT_TIMEOUT, 20);curl_setopt($curl2, CURLOPT_USERAGENT, "Mozilla/4.0 (compatible; MSIE 5.01; Windows NT 5.0)");curl_setopt($curl2, CURLOPT_FOLLOWLOCATION, false);curl_setopt($curl2, CURLOPT_URL, "http://$url/download.php?exec=testrs");$xxx2 = curl_exec($curl2);$check2 = explode('connected',$xxx2);if (isset($check2[1])) {	    mysql_query("update servers set active='1' where Id='$Id'") or die(mysql_error()); 		$workingrs = "1";		curl_close($curl2);	}	else	{		mysql_query("update servers set active='0' where Id='$Id'") or die(mysql_error()); 		$workingrs = "0";		curl_close($curl2);			$EmailTo = $youremail;			$Subject = "Server $name ($Id) Offline";			$Body = "Server $name ($Id)  offline ($url) - $name - Rapidshare";			mail($EmailTo, $Subject, $Body, "From: <LH RapidShare>");	}//die("expired");//die($check1[1]);}else{	mysql_query("update servers set active='0' where Id='$Id'") or die(mysql_error()); 	$working = "0";	curl_close($curl);			$EmailTo = $youremail;			$Subject = "Server $name ($Id)  Offline";			$Body = "Server $name ($Id)  offline ($url) - $name - Offline or mysql error";			mail($EmailTo, $Subject, $Body, "From: <LH RapidShare>");}		?>		<td><?php echo "<img src=\"images/$working.png\" alt=\"$working\" /> - <img src=\"images/$workingrs.png\" alt=\"$workingrs\" />"; ?></td>		<td><?php echo "<a href=\"http://$url/download.php?id=abc\" target=\"_blank\">Visit</a>"; ?></td>	</tr><?php}?></table><br /><br /><img src="images/1.png" alt="1" /> - <img src="images/1.png" alt="1" /> = Host OK - Rapidshare OK<br /><img src="images/1.png" alt="1" /> - <img src="images/0.png" alt="0" /> = Host OK - Rapidshare NOT OK<br /><img src="images/0.png" alt="0" /> - <img src="images/0.png" alt="0" /> = Host NOT OK - Rapidshare NOT OK<br /></center><?phpmysql_close($mysql); }?>